import{gsap}from"gsap";function heroAnimation(){var t=gsap.timeline();return t.from("#first-line",{duration:.5,alpha:0,y:-100}).from("#second-line",{duration:.5,alpha:0,y:-50}).from("#trail-btn",{duration:.5,alpha:0,y:50}).from("#trail-btn i",{duration:.25,alpha:0,rotation:90}),t}gsap.set("button i",{transformOrigin:"center bottom"});var mainTL=gsap.timeline();mainTL.add(heroAnimation);let mm=gsap.matchMedia();var buttonSize=1;mm.add("(min-width: 800px)",(()=>{buttonSize=2})),mm.add("(max-width: 799px)",(()=>{buttonSize=1.25}));var buttonAnimation=gsap.timeline({paused:!0});buttonAnimation.to("#trail-btn",{duration:.25,scale:buttonSize}).to("#trail-btn i",{duration:.25,rotateY:180}).to("#first-line",{duration:.25,alpha:0,y:50}).to("#second-line",{duration:.25,alpha:0,y:25});let button=document.querySelector("#trail-btn");button.addEventListener("mouseover",(function(){buttonAnimation.play()})),button.addEventListener("mouseout",(function(){buttonAnimation.reverse()}));